(5, 6, '_form', '<div class="clearfix">\n<p>Your Name (required)<br />\n    [text* your-name class:form-control] </p>\n\n<p>Your Email (required)<br />\n    [email* your-email class:form-control] </p>\n\n<p>Phones<br />\n    [text your-phone class:form-control] </p>\n\n<p>Subject<br />\n    [text your-subject class:form-control] </p>\n\n<p>Your Message<br />\n    [textarea your-message class:form-control] </p>\n<div>[recaptcha]</div>\n<p><br/>\n[submit class:btn class:btn-default class:btn-success"Send"]\n</p>\n</div>'),
(6, 6, '_mail', 'a:8:{s:7:"subject";s:30:"Labib Academy "[your-subject]"";s:6:"sender";s:29:"[your-name] <admin@gmail.com>";s:4:"body";s:167:"From: [your-name] <[your-email]>\nSubject: [your-subject]\n\nMessage Body:\n[your-message]\n\n--\nThis e-mail was sent from a contact form on Labib Academy (http://localhost)";s:9:"recipient";s:15:"admin@gmail.com";s:18:"additional_headers";s:22:"Reply-To: [your-email]";s:11:"attachments";s:0:"";s:8:"use_html";b:0;s:13:"exclude_blank";b:0;}'),
